import React,{useContext} from 'react';
import {ThemeContext}from './ThemeProvider';
import './App.css';

function App(){
    const{toggle,toggleFunction}=useContext(ThemeChanger);
    return (
        <div className='app' style={{backgorundColor:toggle?'grey':'white'}}>
            <h1>Context API</h1>
            <button onClick={toggleFunction}>Change</button>
        </div>
    );
}

export default App;
