import React,{useContext} from 'react';
import {ThemeChanger, ThemeContext} from './ThemeChanger';
import './App.css';

function App(){
    const{toggle,toggleFunction}=useContext(ThemeContext)
    return (
        <div className='app' style={{backgorundColor:toggle?'grey':'white'}}>
            <h1>Context API</h1>
        </div>
    );
}

export default App;
